<?phpnamespace Admin\Controller;class IndexController extends RootController{	/**	 * 加载框架	 */	public function index(){		$admin_nav = C('ADMIN_NAV');		$this->assign('admin_nav',$admin_nav);		//配置信息		$setting = D('Setting')->getField('key,value');		$this->assign('setting',$setting);		$this->display();	}		/**	 * 默认页	 */	public function index_index(){		//文章条数		$count_news = M('Article')->count();		$this->assign("news_count",$count_news);		//留言条数		$count_guestbook = M('Guestbook')->count();		$this->assign("guestbook_count",$count_guestbook);		//广告位条数		$count_ad = M('Ad')->count();		$this->assign("ad_count",$count_ad);		//管理员个数		$count_manager= M('Manager')->count();		$this->assign("manager_count",$count_manager);		//登录信息			$this->display();	}	}